hash_table = {}

class M424:
    def characterReplacement(self, s: str, k: int) -> int:
        letters = set([c for c in s])
        global_max = 0
        for letter in letters:
            rear = 0
            chances = k
            for front in range(len(s)):
                if s[front]!=letter:
                    chances -= 1
                if chances < 0:
                    if s[rear] != letter:
                        chances += 1
                    rear += 1
            global_max = max(global_max, front - rear + 1)
        return global_max
            

a = M424()

print(a.characterReplacement('ABAB', 2)) # 4
print(a.characterReplacement("AABABBA", 1))  # 4
print(a.characterReplacement("SDSSMESSTR", 2))  # 6
print(
    a.characterReplacement(
        "IMNJJTRMJEGMSOLSCCQICIHLQIOGBJAEHQOCRAJQMBIBATGLJDTBNCPIFRDLRIJHRABBJGQAOLIKRLHDRIGERENNMJSDSSMESSTR",
        2,
    )
)  # 6 SDSSMESSTR
print(a.characterReplacement("AAAA", 2))  # 4
print(
    a.characterReplacement(
        "",
        7886,
    )
)  # 10000

